INSERT INTO `tb_alternatif` (`id`, `kode_alternatif`, `nama_wisata`, `alamat`, `kota`, `jenis_wisata`, `deskripsi`, `latitude`, `longitude`, `link_gmaps`, `jarak_default`, `biaya_masuk`, `biaya_normalisasi`, `aksesibilitas`, `fasilitas`, `rating`, `created_at`, `updated_at`) VALUES
(1, 'A1', 'Air Terjun Coban Rondo', 'Jl. Coban Rondo No.30, Krajan, Pandesari, Kec. Pujon, Kabupaten Malang, Jawa Timur 65391', 'Kabupaten Malang', 'alam', 'Air Terjun Coban Rondo merupakan air terjun yang terletak di Kecamatan Pujon, Kabupaten Malang, Jawa Timur. Air terjun ini mudah dijangkau oleh kendaraan umum.', -7.89540000, 112.48320000, 'https://maps.app.goo.gl/CRzNv8JS9R66gXHJ8', 114.00, 35000, 35.00, 5.00, 5.00, 4.50, NULL, '2025-11-26 18:24:52'),
(2, 'A2', 'Air Terjun Coban Talun', 'Dusun Wonorejo, Desa Tulungrejo, Kecamatan Bumiaji, Kota Batu, Jawa Timur. ', 'Batu', 'alam', 'Air Terjun Coban Talun adalah destinasi wisata alam populer yang terletak di Dusun Wonorejo, Desa Tulungrejo, Kecamatan Bumiaji, Kota Batu. Air terjun ini memiliki ketinggian sekitar 75 meter dengan debit air yang deras dan dikelilingi oleh hutan pinus yang asri, menciptakan udara yang sangat sejuk.\n\nKawasan Coban Talun dikenal sebagai tempat wisata yang terintegrasi, tidak hanya menawarkan keindahan air terjun, tetapi juga berbagai spot foto instagramable dan area penginapan unik.', -7.80203890, 112.51704850, 'https://maps.app.goo.gl/paLMvCEHqGNPg6vY9', 110.00, 10000, 10.00, 4.00, 5.00, 4.60, NULL, NULL),
(3, 'A3', 'Air Terjun Dolo', '4RHP+R4W, Dusun Besuki, Desa Jugo, Kecamatan Mojo, Hutan, Blimbing, Kediri, Kabupaten Kediri, Jawa Timur 64162', 'Kabupaten Kediri', 'alam', 'Air Terjun Dolo adalah destinasi wisata alam yang menawan di Dusun Besuki, Desa Jugo, Kecamatan Mojo, Kabupaten Kediri, berada di ketinggian sekitar 1.800 mdpl di lereng timur Gunung Wilis.\n\nAir terjun ini memiliki ketinggian sekitar 125 meter yang terbagi dalam tiga tingkatan. Meskipun debit airnya tidak terlalu deras, airnya sangat jernih dan dingin khas pegunungan. Lokasi yang dikelilingi hutan dan pepohonan lebat menawarkan udara segar dan suasana yang tenang, sering kali diselimuti kabut, menambah kesan magis.\n\nDaya tarik utama tempat ini adalah keindahan alamnya dan jalur trekking yang menantang namun disuguhi pemandangan hutan yang asri sepanjang perjalanan meniti anak tangga menuju ke bawah air terjun.', -7.87063730, 111.83543980, 'https://maps.app.goo.gl/qSV7PQtkcpRhW9EW9', 142.00, 7000, 7.00, 3.00, 5.00, 4.40, NULL, NULL),
(4, 'A4', 'Air Terjun Tumpak Sewu', 'Jl. Raya Dampit-Lumajang, Besukcukit, Sidomulyo, Kec. Pronojiwo, Kabupaten Lumajang, Jawa Timur 67374', 'Kabupaten Lumajang', 'alam', 'Air Terjun Tumpak Sewu, yang dijuluki sebagai \"Niagara dari Indonesia\", adalah air terjun yang sangat unik karena bentuknya yang menyerupai tirai air lebar yang mengalir dari tebing melingkar. Air terjun setinggi kurang lebih 120 meter ini memiliki puluhan hingga ratusan aliran air kecil yang berasal dari mata air di lereng selatan Gunung Semeru.', -8.23893110, 112.92142620, 'https://maps.app.goo.gl/EAhimnnVfjTw4tv3A', 157.00, 20000, 20.00, 2.00, 5.00, 4.80, NULL, NULL),
(5, 'A5', 'Alun-Alun Banyuwangi', 'Q9RF+6GF, Temenggungan, Kecamatan Banyuwangi, Kabupaten Banyuwangi, Jawa Timur 68411', 'Banyuwangi', 'buatan', 'Alun-Alun Banyuwangi, yang juga dikenal sebagai Taman Blambangan, adalah ruang terbuka hijau utama dan landmark kota yang terletak di pusat pemerintahan Kabupaten Banyuwangi. Alun-alun ini dikenal rapi, modern, dan terawat dengan baik.\n\nFungsinya bukan hanya sebagai taman kota, tetapi juga sebagai pusat kegiatan publik dan olahraga, dilengkapi dengan fasilitas seperti jogging track, lapangan olahraga, dan area bermain anak. Kawasan ini selalu ramai, terutama saat akhir pekan, karena menjadi titik kumpul masyarakat untuk bersantai, berolahraga, dan menikmati aneka kuliner lokal.\n\nAlun-alun ini juga menjadi tempat rutin diselenggarakannya kegiatan mingguan seperti Car Free Day dan berbagai festival seni budaya yang menjadi ciri khas kota Banyuwangi, menjadikannya pusat interaksi sosial yang vital bagi warga dan wisatawan.', -8.20737690, 114.37592910, 'https://maps.app.goo.gl/4xpSxUCxD1MKsaoq6', 299.00, 0, 0.00, 5.00, 5.00, 4.70, NULL, NULL),
(6, 'A6', 'Alun-Alun Blitar', 'Jl. Merdeka, Kepanjen Lor, Kec. Kepanjenkidul, Kota Blitar, Jawa Timur 66117', 'Blitar', 'buatan', 'Alun-Alun Kota Blitar adalah jantung dan pusat keramaian kota Blitar, berfungsi sebagai ruang terbuka hijau multifungsi. Tempat ini dikelilingi oleh bangunan-bangunan penting, termasuk Masjid Agung di sisi barat. Alun-alun ini populer di kalangan warga lokal dan wisatawan sebagai lokasi ideal untuk berolahraga, bersantai, dan menikmati wisata kuliner murah.\n\nDikenal dengan area berumputnya yang terawat, jogging track, dan berbagai fasilitas rekreasi gratis seperti taman bermain dan alat kebugaran. Pada malam hari, area ini menjadi sangat hidup dengan berbagai pedagang kaki lima yang menjajakan makanan dan minuman.', -8.09817080, 112.16517580, 'https://maps.app.goo.gl/gnAwznCgwyfNGKz88', 168.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(7, 'A7', 'Alun-Alun Bojonegoro', 'VV2J+73W, Jl. Imam Bonjol, Ledok Kulon Tiga, Alun-alun, Kec. Bojonegoro, Kabupaten Bojonegoro, Jawa Timur 62111', 'Bojonegoro', 'buatan', 'Alun-Alun Bojonegoro adalah pusat kegiatan publik dan jantung kota Bojonegoro. Tempat ini dikenal sebagai salah satu ruang terbuka hijau yang paling terawat dan menjadi lokasi favorit warga untuk rekreasi, olahraga, dan bersantai.\n\nAlun-alun ini memiliki area yang luas, dilengkapi dengan jogging track, lapangan terbuka, dan fasilitas bermain anak. Pada sore hari hingga malam, alun-alun ini menjadi sangat ramai dan merupakan pusat kuliner kaki lima yang menawarkan berbagai jajanan khas Bojonegoro. Tempat ini secara rutin digunakan untuk kegiatan pemerintahan, seni, dan budaya lokal.', -7.14909350, 111.88025090, 'https://maps.app.goo.gl/aJuK6BwxkbiaUyZb8', 137.00, 0, 0.00, 5.00, 5.00, 4.40, NULL, NULL),
(8, 'A8', 'Alun-Alun Bondowoso', '3RPF+P2W, Kauman, Kotakulon, Kec. Bondowoso, Kabupaten Bondowoso, Jawa Timur 68211', 'Bondowoso', 'buatan', 'Alun-Alun Bondowoso, yang secara resmi dikenal sebagai Alun-Alun Raden Bagus Asra (RBA), merupakan pusat aktivitas masyarakat dan jantung kota. Alun-alun ini berfungsi sebagai ruang publik terbuka yang menjadi lokasi utama untuk kegiatan rekreasi, olahraga, dan interaksi sosial.\n\nMeskipun pernah direncanakan revitalisasi besar-besaran, alun-alun ini tetap menjadi tempat yang ramai, terutama pada malam hari di mana area di sekitarnya dipenuhi oleh pedagang kaki lima (PKL) yang menjual aneka kuliner. Kawasan ini juga menjadi tempat penyelenggaraan berbagai event dan pameran.', -7.91319780, 113.82246040, 'https://maps.app.goo.gl/ZCHBcwkGQEJuYM6m9', 201.00, 0, 0.00, 4.00, 5.00, 4.40, NULL, NULL),
(9, 'A9', 'Alun-Alun Gresik', 'Jl. KH. Wachid Hasyim, Bedilan, Kec. Gresik, Kabupaten Gresik, Jawa Timur 61114', 'Gresik', 'buatan', 'Alun-Alun Gresik adalah ikon kota Gresik yang dikenal sebagai \"Kota Santri\". Dengan luas sekitar 8.123 meter persegi, alun-alun ini berfungsi sebagai ruang terbuka hijau yang sejuk, nyaman, dan ramah kantong. Alun-alun ini memiliki desain modern dengan nuansa Islami yang kental dan dikelilingi oleh pepohonan rindang.\n\nAlun-alun ini sangat populer, terutama pada malam hari, di mana banyak warga dan wisatawan datang untuk bersantai, berolahraga, dan menikmati beragam pilihan kuliner lezat dari PKL di sekitarnya. Letaknya yang bersebelahan dengan Masjid Jami\' Gresik menjadikannya pusat kegiatan sosial dan keagamaan.', -7.15713260, 112.65604630, 'https://maps.app.goo.gl/RhGMktwLAjJ4Ac5q9', 39.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(10, 'A10', 'Alun-Alun Jember', 'Kaliwates, Jl. PB Sudirman, Kp. Using, Jemberlor, Kec. Patrang, Kabupaten Jember, Jawa Timur 68131', 'Jember', 'buatan', 'Alun-Alun Jember, yang kini mengusung konsep Jember Nusantara, adalah ikon baru Kabupaten Jember setelah menjalani revitalisasi besar. Alun-alun ini berfungsi sebagai jantung kota dan ruang terbuka publik yang modern.\n\nTempat ini tidak hanya menyediakan area rekreasi dan olahraga, tetapi juga mengintegrasikan kegiatan sosial, ekonomi, dan budaya. Dengan adanya koneksi ke Gedung Jember Nusantara (pusat UMKM) dan pusat kuliner melalui infrastruktur baru, Alun-Alun Jember didesain untuk menjadi pusat aktivitas terpadu yang mempromosikan potensi lokal sekaligus memudahkan mobilitas warga dan wisatawan.', -8.16890050, 113.70216490, 'https://maps.app.goo.gl/rDWmhg9YLoUfYFus7', 195.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(11, 'A11', 'Alun-Alun Kediri', 'Jl. Panglima Sudirman, Kp. Dalem, Kec. Kota, Kota Kediri, Jawa Timur 64129', 'Kediri', 'buatan', 'Alun-Alun Kota Kediri adalah ikon kota yang berfungsi sebagai paru-paru kota dan pusat kegiatan masyarakat. Alun-alun ini dikenal dengan lanskap tamannya yang indah dan teduh, serta keberadaan patung di bagian tengahnya. Lokasinya sangat strategis karena berada di jantung kota, berdekatan dengan Masjid Agung. Setelah menjalani proyek revitalisasi, alun-alun ini semakin ramai dikunjungi sebagai destinasi rekreasi keluarga, olahraga pagi (jogging dan senam), serta wisata kuliner yang hidup pada malam hari.', -7.82656290, 112.01105950, 'https://maps.app.goo.gl/r98PZWYsoaEm3oyY9', 104.00, 0, 0.00, 4.00, 5.00, 4.30, NULL, NULL),
(12, 'A12', 'Alun-Alun Kota Wisata Batu', 'Alun - Alun Kota Wisata Batu, Jl. Sudiro, Sisir, Kec. Batu, Kota Batu, Jawa Timur 65314', 'Batu', 'buatan', 'Alun-Alun Kota Wisata Batu adalah taman kota yang sangat populer dan menjadi pusat keramaian di Kota Batu. Terletak strategis di pusat kota, alun-alun ini dikenal dengan suasana pegunungan yang sejuk dan dilengkapi dengan ikon utama berupa Bianglala raksasa yang berwarna-warni. Selain Bianglala, tempat ini menawarkan berbagai wahana anak yang terjangkau, area bersantai yang teduh, dan menjadi sentra kuliner malam hari.\n\nAlun-alun ini beroperasi 24 jam dan sering menjadi tujuan utama wisatawan untuk beristirahat setelah mengunjungi berbagai objek wisata di Kota Batu, menjadikannya destinasi ramah keluarga dan sangat terjangkau karena tiket masuknya gratis.', -7.87117470, 112.52516870, 'https://maps.app.goo.gl/Qn3kp65d88xyYiQY6', 104.00, 0, 0.00, 4.00, 5.00, 4.70, NULL, NULL),
(13, 'A13', 'Alun-Alun Kota Malang', 'Jl. Merdeka Selatan, Kiduldalem, Kec. Klojen, Kota Malang, Jawa Timur 65119', 'Malang', 'buatan', 'Alun-Alun Kota Malang, atau juga dikenal sebagai Alun-Alun Merdeka, merupakan salah satu ruang terbuka hijau (RTH) paling populer dan bersejarah di Malang. Terletak di pusat kota yang strategis, alun-alun ini menjadi titik kumpul utama dan destinasi wisata gratis bagi warga lokal maupun wisatawan. Daya tarik utamanya adalah area taman yang asri, pepohonan rindang, dan fasilitas air mancur warna-warni yang menjadi pusat perhatian. Dengan fasilitas lengkap seperti playground dan WiFi gratis, tempat ini sangat cocok untuk bersantai, berolahraga, atau menikmati aneka kuliner murah di sekitarnya.', -7.98261350, 112.63082340, 'https://maps.app.goo.gl/E3o6End68DjA7nW4A', 94.00, 0, 0.00, 4.00, 5.00, 4.50, NULL, NULL),
(14, 'A14', 'Alun-Alun Tugu Malang', '2JFM+4JV, Jl. Tugu, Kiduldalem, Kec. Klojen, Kota Malang, Jawa Timur 65111', 'Malang', 'buatan', 'Alun-Alun Tugu Malang adalah ikon sejarah dan pusat pemerintahan Kota Malang. Berbeda dengan Alun-Alun Kota Malang (Alun-Alun Merdeka), alun-alun ini berbentuk bundaran yang terletak di depan Balai Kota, menjadikannya pusat perhatian dengan latar belakang bangunan bergaya arsitektur kolonial yang megah.\n\nCiri khas utamanya adalah Tugu Pahlawan di tengahnya yang dikelilingi oleh kolam teratai dan taman bunga yang sangat terawat, sehingga sering juga disebut \"Taman Bunga\". Tempat ini sangat cocok untuk bersantai, berjalan kaki, berolahraga pagi, atau menikmati suasana kota yang asri dan bersejarah.', -7.98261350, 112.63082340, 'https://maps.app.goo.gl/27SUw1GZrkG4LmoE8', 93.00, 0, 0.00, 4.00, 5.00, 4.60, NULL, NULL),
(15, 'A15', 'Alun-Alun Lamongan', 'Alun-alun lamongan, Jl. Lamongrejo, Tumenggungan, Kec. Lamongan, Kabupaten Lamongan, Jawa Timur 62214', 'Lamongan', 'buatan', 'Taman Alun-Alun Kota Lamongan adalah ruang terbuka hijau (RTH) utama dan terbesar di Kabupaten Lamongan. Setelah direnovasi, alun-alun ini menjadi daya tarik rekreasi keluarga yang semakin populer. Ciri khas utamanya adalah monumen pesawat patroli udara TNI AU yang gagah berdiri di tengah taman, serta adanya Bianglala raksasa yang menawarkan pemandangan kota dari ketinggian, terutama saat malam hari dengan hiasan lampu berwarna-warni. Berbagai fasilitas dan wahana bermain tersedia, menjadikannya pusat keramaian dan kuliner yang nyaman.', -7.12021300, 112.41571890, 'https://maps.app.goo.gl/FB9YqTGQFAqHF3eM8', 79.00, 0, 0.00, 4.00, 5.00, 4.50, NULL, NULL),
(16, 'A16', 'Alun-Alun Lumajang', 'V67F+WQF, Jl. Alun-Alun Utara, Rogotrunan, Jogoyudan, Kec. Lumajang, Kabupaten Lumajang, Jawa Timur 67316', 'Lumajang', 'buatan', 'Alun-Alun Lumajang adalah ruang terbuka publik sekaligus ikon Kabupaten Lumajang. Terletak strategis di pusat kota, berseberangan langsung dengan Masjid Agung dan dekat dengan Kantor Bupati, alun-alun ini menjadi pusat interaksi sosial, hiburan, dan kegiatan olahraga bagi masyarakat.\n\nAlun-alun ini menawarkan suasana yang asri dengan penataan taman, patung, dan air mancur. Ramai dikunjungi pagi hari untuk berolahraga, dan malam hari untuk menikmati aneka kuliner lokal. Saat ini, Alun-Alun Lumajang sedang dalam proses revitalisasi untuk membenahi fasilitas dan meningkatkan aksesibilitasnya, termasuk penambahan jalur ramah disabilitas, demi menciptakan wajah baru yang lebih modern, nyaman, dan ramah bagi seluruh lapisan masyarakat.', -8.13522010, 113.22444320, 'https://maps.app.goo.gl/QwnZWd6rkpXhVTE68', 147.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(17, 'A17', 'Alun-Alun Madiun', 'Pangongangan, Jl. Kolonel Marhadi No.12, Nambangan Lor, Kec. Manguharjo, Kota Madiun, Jawa Timur 63121', 'Madiun', 'buatan', 'Alun-Alun Kota Madiun adalah jantung dan ikon kota, yang telah ditata menjadi ruang terbuka hijau (RTH) yang modern, rapi, dan fungsional. Tempat ini menonjol karena keberadaan replika Tugu Monas setinggi 24 meter, menjadikannya spot foto populer. Selain sebagai pusat rekreasi dan olahraga, alun-alun ini juga berfungsi sebagai pusat edukasi dengan adanya Taman Pintar (perpustakaan mini) dan pusat kuliner legendaris Madiun, terutama pada malam hari yang ramai dengan pasar malam dan gemerlap lampion.', -7.62917920, 111.51683070, 'https://maps.app.goo.gl/F4iV3ccjMmQTYZvj8', 162.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(18, 'A18', 'Alun-Alun Mojokerto', 'Jl. Veteran, Mergelo, Magersari, Kec. Magersari, Kota Mojokerto, Jawa Timur 61324', 'Mojokerto', 'buatan', 'Alun-Alun Kota Mojokerto adalah ruang terbuka hijau (RTH) utama dan pusat aktivitas masyarakat di Kota Mojokerto. Alun-alun ini dikenal karena memiliki desain modern dengan banyak spot foto menarik, area bermain anak yang luas, dan taman yang asri. Ikon utamanya adalah tugu perjuangan di tengah alun-alun dan kolam air mancur. Alun-alun ini berfungsi sebagai pusat rekreasi keluarga, lokasi olahraga, dan sentra kuliner yang ramai pada malam hari. Setelah direvitalisasi, Alun-Alun Mojokerto menjadi salah satu yang paling nyaman dan tertata di Jawa Timur.', -7.46323940, 112.43196420, 'https://maps.app.goo.gl/TJDzHYuCX8aygPam7', 50.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(19, 'A19', 'Alun-Alun Ngawi', 'Kluncing, Ketanggi, Ngawi, Kabupaten Ngawi, Jawa Timur 63211', 'Ngawi', 'buatan', 'Alun-Alun Ngawi merupakan wajah baru Kabupaten Ngawi. Setelah mengalami revitalisasi, alun-alun ini bertransformasi menjadi taman kota yang modern, bersih, dan estetik. Ciri khasnya adalah taman yang luas dengan hamparan bunga yang cantik, air mancur, dan tugu lambang kota. Alun-alun ini tidak hanya berfungsi sebagai tempat rekreasi dan olahraga, tetapi juga sebagai pusat kuliner dan spot berkumpul yang nyaman. Keberadaan jalur ramah disabilitas dan fasilitas yang lengkap menjadikan Alun-Alun Ngawi destinasi yang inklusif untuk seluruh lapisan masyarakat.', -7.40360790, 111.44459750, 'https://maps.app.goo.gl/68xQ2V3K5G4jMyRL6', 182.00, 0, 0.00, 5.00, 5.00, 4.50, NULL, NULL),
(20, 'A20', 'Alun-Alun Pacitan', 'Jl. Sugiyo Pranoto No.1, RT.1/RW.01, Krajan, Kec. Pacitan, Kabupaten Pacitan, Jawa Timur 63511', 'Pacitan', 'buatan', 'Alun-Alun Pacitan adalah pusat kota dan titik nol kilometer Kabupaten Pacitan. Alun-alun ini dikenal dengan suasana yang hidup, terutama saat malam hari ketika dihiasi lampu-lampu warna-warni di sekitar air mancurnya. Tempat ini menjadi destinasi rekreasi keluarga, lokasi olahraga pagi, dan sentra kuliner yang menyajikan berbagai makanan khas. Letaknya yang strategis, dekat dengan Masjid Agung dan kantor pemerintahan, menjadikannya pusat interaksi sosial masyarakat.', -8.19469280, 111.10316210, 'https://maps.app.goo.gl/hrbiVV8JmYpt7tfH7', 268.00, 0, 0.00, 4.00, 5.00, 4.50, NULL, NULL),
(21, 'A21', 'Alun-Alun Pasuruan', '9W54+6MP, Jl. Alun-Alun Sel., Kebonsari, Kec. Panggungrejo, Kota Pasuruan, Jawa Timur 67116', 'Pasuruan', 'buatan', 'Alun-Alun Pasuruan adalah pusat kota yang berfungsi sebagai ruang terbuka hijau (RTH) utama, sekaligus menjadi bagian dari kawasan wisata religi terintegrasi. Alun-alun ini dikenal dengan suasana malam hari yang meriah dan indah dengan hiasan lampu, serta air mancur di tengahnya. Lokasi ini sangat strategis karena bersebelahan langsung dengan Masjid Jami\' Al-Anwar, dan dekat dengan makam Kyai Hamid, menjadikannya salah satu ikon dari city branding Pasuruan sebagai \"Madinah van Java\". Alun-alun ini merupakan destinasi wisata keluarga yang gratis dan ramah anak.', -7.64188790, 112.90667930, 'https://maps.app.goo.gl/WH8osXp37wiy4pf78', 68.00, 0, 0.00, 4.00, 5.00, 4.50, NULL, NULL),
(22, 'A22', 'Alun-Alun Ponorogo', '4FH6+9Q5, Ponorogo, Tamanarum, Ponorogo, Kabupaten Ponorogo, Jawa Timur', 'Ponorogo', 'buatan', 'Alun-Alun Ponorogo adalah jantung kota dan ruang terbuka hijau (RTH) utama di Kabupaten Ponorogo. Alun-alun ini sangat ikonik karena merupakan pusat penyelenggaraan kegiatan budaya Reog Ponorogo, yang sering diadakan di panggung utamanya. Selain sebagai pusat budaya, tempat ini juga berfungsi sebagai pusat olahraga, rekreasi keluarga, dan sentra kuliner yang hidup pada sore dan malam hari. Dengan penataan taman dan fasilitas yang baik, Alun-Alun Ponorogo menjadi destinasi yang wajib dikunjungi di kota Reog.', -7.87104760, 111.46226300, 'https://maps.app.goo.gl/1o4vtSWyZh8XchMe9', 192.00, 0, 0.00, 5.00, 5.00, 4.60, NULL, NULL),
(23, 'A23', 'Alun-Alun Probolinggo', '7648+88Q, Jalan KH Mas Mansyur, Mangunharjo, Sukabumi, Kec. Mayangan, Kota Probolinggo, Jawa Timur 67219', 'Probolinggo', 'buatan', 'Alun-Alun Probolinggo adalah ikon kota yang berfungsi sebagai jantung wisata urban dan ruang terbuka hijau yang ramah keluarga. Setelah direvitalisasi, alun-alun ini tampil lebih terbuka, memiliki fasilitas yang lengkap, termasuk wahana permainan anak, jalur jogging, dan patung-patung menarik. Lokasinya sangat strategis, berada di dekat Stasiun Kereta Api, Museum Probolinggo, dan Masjid Agung Raudlatul Jannah. Alun-alun ini dikenal ramai, terutama pada pagi hari untuk olahraga dan malam hari sebagai sentra kuliner dan hiburan yang murah meriah.', -7.74413630, 113.21584480, 'https://maps.app.goo.gl/wR5hJ6rJ19V7KDdq6', 106.00, 0, 0.00, 5.00, 5.00, 4.30, NULL, NULL),
